INSERT INTO app.actu (title, text, date, enddate, year, file)
VALUES ('EXPOSITION ANNUELLE IMAGIN''WOO',
        'Imagin’Woo, le club photo de Waterloo vous invite à sa 8ème exposition photographique... Couleurs ou noir et blanc ? Voilà un débat qu’ont décidé de mettre sur le devant de la scène les membres du club Mieux : ils en ont fait le thème de leur nouvelle et 8e exposition baptisée "Versus".',
        "2023-10-20", "2023-11-05", 2023, 'expo2023.jpg'),

       ('EXPOSITION ANNUELLE IMAGIN''WOO',
        'Imagin’Woo, le club photo de Waterloo a atteint l’âge de raison. Sept ans déjà qu’il réunit ses membres, tous passionnés de photos, et animés par l’envie de partage et de découvertes. Alors que les éditions 2020 et 2021 des expositions avaient, crise sanitaire oblige, présenté des travaux très individuels, le club a souhaité pour 2022 se lancer dans un réel exercice collectif. Une recherche qui a mobilisé l’ensemble des membres tout au long de l’année, l’idée était d’aboutir, en plus de l’exposition annuelle, à la réalisation d''un magnifique livre. Le livre est disponible sur le site du Livre en papier au prix coûtant de 25,00 eur cliquez ici pour y accéder',
        "2023-11-12","2022-11-26", 2022, 'expo2022.jpg')